  Widget _buildTocSubItem(String title, String page) {
    return Padding(
      padding: EdgeInsets.only(left: 16, top: 2, bottom: 2),
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Expanded(
            child: Text(title, style: pjsStyleBlack12400),
          ),
          Text(page, style: pjsStyleBlack12400),
        ],
      ),
    );
  }
